import {IdType, Netverify, PerformMetadata, PerformPayload, Region, VerificationError} from '../src';
import {config} from './config';

/**
 * Example Integration
 *
 * The sample below demonstrates how to prepare and execute a verification.
 * Please refer to the section "Example Integration" in "../README.md" for instructions on
 * how to configure and run this sample code.
 */

// Set up metadata for the API request.
const metadata: PerformMetadata = {
  region: Region.US,
  apiToken: config.apiToken,
  apiSecret: config.apiSecret,
  userAgent: config.userAgent
};

// It is also possible to provide a specific API host for testing purposes.
// If this is not set, the default production host for the given region will automatically be chosen.
if (config.hostname) {
  metadata.hostname = config.hostname;
}

// Set up the payload for the API request.
const payload: PerformPayload = {
  merchantIdScanReference: 'myScanRef',
  frontsideImage:
    '',
  faceImage:
    '',
  country: 'AUT',
  idType: IdType.ID_CARD
};

// Perform the verification and handle the outcome.
new Netverify()
  .perform(payload, metadata)
  .then(result => {
    console.log(`Verification complete.\nScan reference: ${result.jumioIdScanReference}\nTimestamp: ${result.timestamp}`);
  })
  .catch((e: VerificationError) => {
    console.log(`${e.type}: ${e.message}`);
  });
